"""This is an example of the shapely library used for dilate a route(line)"""

from shapely.ops import polygonize
from shapely.geometry import Polygon
from shapely.geometry import MultiPolygon
from shapely.geometry import MultiLineString
from shapely.geometry import CAP_STYLE, JOIN_STYLE
from shapely.geometry import LineString
from matplotlib import pyplot
from descartes import PolygonPatch
from figures import SIZE, BLUE, GRAY, set_limits, plot_line
from shapely.geometry import Point
from shapely.geometry.polygon import Polygon

CAP_STYLE.flat
JOIN_STYLE.bevel


def plot_coords(coords):
    #print(coords)
    line = LineString(coords)
    #print(line)
    #dilated = line.buffer(0.5)
    fig = pyplot.figure(1, figsize=SIZE, dpi=90)

    # 1
    ax = fig.add_subplot(121)

    plot_line(ax, line)

    # 360Â° degrees is equal to 40075 km
    # linebuffer the first parameter is in degrees
    dilated = line.buffer(0.0179663132, resolution=1, cap_style=3)
    print(f'value{dilated}')
    patch1 = PolygonPatch(dilated, fc=BLUE, ec=BLUE, alpha=0.5, zorder=2)
    ax.add_patch(patch1)
    x = 4.65774
    y = -74.1166

    point = Point(y, x)
    print(point.within(dilated))
    print(dilated.contains(point))

    ax.set_title('a) dilation, cap_style=3')

    set_limits(ax, -72, -75, 4, 6)

    # 2
    ax = fig.add_subplot(122)

    patch2a = PolygonPatch(dilated, fc=GRAY, ec=GRAY, alpha=0.5, zorder=1)
    ax.add_patch(patch2a)

    pyplot.show()


